Style.Rule:
		&maxscale_zoom16;
		&minscale_zoom18;
 		([amenity] = 'parking') and ([fee] = 'yes') and not ([access] = 'private' or [access] = 'restricted' or [access] = 'permissive' or [access] = 'customers' or [access] = 'no')

		&maxscale_zoom16;
		&minscale_zoom18;
 		([amenity] = 'parking') and ([fee] != 'yes') and not ([access] = 'private' or [access] = 'restricted' or [access] = 'permissive' or [access] = 'customers' or [access] = 'no')
